var interval, timer = function() {
    interval = setInterval(function() {
        var e = document.getElementsByClassName("slide"),
            t = $("#slider").children(".active").index();
        $(e).each(function() {
            $(this).removeClass("active")
        }), t == e.length - 1 ? t = 0 : t++, $(e[t]).addClass("active")
    }, 6e3)
};

function slideNav(e) {
    var t = document.getElementsByClassName("slide"),
        a = $("#slider").children(".active").index();
    e > 0 ? a == t.length - 1 ? a = 0 : a += e : 0 == a ? a = t.length - 1 : a += e, $(t).each(function() {
        $(this).removeClass("active")
    }), $(t[a]).addClass("active"), clearInterval(interval), timer()
}
timer();